mapper.select:
SELECT t.id, t.code, t.name, t.parentid, t.innercode, t.mainjob, t.startservetime, t.endservetime, t.staffid, t.orgid, t.deptid, t.orgid, t.psnlcatgid, t.dutyid, t.positionid, t.rankid, t.creator, t.creationtime, t.modifier, t.modifiedtime, t.tenantid, t.sysid, t.enable, t.ts, t.dr FROM bd_main_job t WHERE 1=1
and t.id = #{condition.id, jdbcType=VARCHAR}
and t.staffid = #{condition.staffid, jdbcType=VARCHAR}
and t.code = #{condition.code, jdbcType=VARCHAR}

mapper.delete:
DELETE FROM	bd_main_job
WHERE  1=1
	
		and 1=2
	
	
		
			and id=#{condition.id, jdbcType=VARCHAR}
		
		
			and staffid=#{condition.staffId, jdbcType=VARCHAR}
		
		
			and id in 
			
				#{item}
